import APIUtil from '../utils/APIUtils'
import ZOAUtil from '../utils/ZOAUtil'

export default class TrackingModel {

    static CMD_QUICK_ACTION = 1414;
    static CMD_SERVICES = 1413;
    static clickSmallMSGBtn(oaid) {
        var body = {
            zarSrc: ZOAUtil.getSRC(),
        }
        return new Promise((resolve, reject) => {
            APIUtil.postJSONWithCredentials(process.env.REACT_APP_DOMAIN + `/api/track?action=click-btn-message-with-not-follower&oaId=${oaid}`, JSON.stringify(body), resolve, reject);
        });
    }

    static clickBtn(oaid, cmd) {
        var body = {
            zarSrc: ZOAUtil.getSRC(),
        }
        return new Promise((resolve, reject) => {
            APIUtil.postJSONWithCredentials(process.env.REACT_APP_DOMAIN + `/api/track?action=click-btn&oaId=${oaid}&cmd=${cmd}`, JSON.stringify(body), resolve, reject);
        });
    }

    static viewArticle(oaid) {
        var body = {
            zarSrc: ZOAUtil.getSRC(),
        }
        return new Promise((resolve, reject) => {
            APIUtil.postJSONWithCredentials(process.env.REACT_APP_DOMAIN + `/api/track?action=viewMedia&oaId=${oaid}`, JSON.stringify(body), resolve, reject);
        });
    }

}
