import Modal from "react-responsive-modal";
import "react-responsive-modal/styles.css";
import LangUtil from '../utils/LangUtil';

import "./styles.scss";

const owlClass = "popup";

export function Popup({ oaName, open, onCloseModal, onAgree }) {
  return (
    <Modal
      open={open}
      onClose={onCloseModal}
      center
      showCloseIcon={false}
      styles={{
        modal: {
          background: "rgba(242, 242, 242, 0.94)",
          backdropFilter: "blur(54.3656px)",
          borderRadius: "14px",
          padding: "0",
          maxWidth: "80%"
        },
      }}
    >
      <div className={owlClass}>
        <div className={`${owlClass}__wrapper`}>
          <div className={`${owlClass}__wrapper__title`}>{LangUtil.get('label-unfollow-oa')}</div>
          <p className={`${owlClass}__wrapper__desc`}>
            {LangUtil.get('oa-unfollow-question')} {oaName}?
          </p>
        </div>
        <div className={`${owlClass}__group-btn`}>
          <div
            className={`${owlClass}__group-btn__item left`}
            onClick={onCloseModal}
          >
            {LangUtil.get('btn-cancel')}
          </div>
          <div
            className={`${owlClass}__group-btn__item right`}
            onClick={onAgree}
          >
            {LangUtil.get('label-unfollow-oa')}
          </div>
        </div>
      </div>
    </Modal>
  );
}


export function ErrorPopup({ open, onCloseModal, msg }) {
  return (
    <Modal
      open={open}
      onClose={onCloseModal}
      center
      showCloseIcon={false}
      styles={{
        modal: {
          background: "rgba(242, 242, 242, 0.94)",
          backdropFilter: "blur(54.3656px)",
          borderRadius: "14px",
          padding: "0",
        },
      }}

    >
      <div className={owlClass}>
        <div className={`${owlClass}__wrapper`}>
          <div className={`${owlClass}__wrapper__title`}>Oops!!!</div>
          <p className={`${owlClass}__wrapper__desc`}>
            {msg}
          </p>
        </div>
      </div>
    </Modal>
  );
}

export function OutAppPopup({open, onCloseModal, onAgree }) {
  return (
    <Modal
      open={open}
      onClose={onCloseModal}
      center
      showCloseIcon={false}
      styles={{
        modal: {
          background: "rgba(242, 242, 242, 0.94)",
          backdropFilter: "blur(54.3656px)",
          borderRadius: "14px",
          padding: "0",
        },
      }}

    >
      <div className={owlClass}>
        <div className={`${owlClass}__wrapper`}>
          <div className={`${owlClass}__wrapper__title`}>Oops!!</div>
          <p className={`${owlClass}__wrapper__desc`}>
            Mở Zalo để thực hiện thao tác này?
          </p>
        </div>
        <div className={`${owlClass}__group-btn`}>
          <div
            className={`${owlClass}__group-btn__item left`}
            onClick={onCloseModal}
          >
            Hủy
          </div>
          <div
            className={`${owlClass}__group-btn__item right`}
            onClick={onAgree}
          >
            Mở Zalo
          </div>
        </div>
      </div>
    </Modal>
  );
}
