/* eslint-disable jsx-a11y/anchor-has-content */
import { useEffect, useState } from "react";

import BottomSheet from "./bottom-sheet";
import MuiThemeProvider from "material-ui/styles/MuiThemeProvider";
import OAModel from "./model/OAModel";
import LangUtil from './utils/LangUtil';
import ListArticle from "./article";
import NativeModel from "./model/NativeModel";
import GlobalModel from "./model/GlobalModel";

import { Menu, OA } from './interface/index'



import "./resource/App.scss";
import { Popup, ErrorPopup, OutAppPopup } from "./popup";
import { StatusStore } from "./component";
import TrackingModel from "./model/TrackingModel";
const owlClass = "App";


const Empty = ({ openProfileOA }: any) => {
  const owlClass = "empty";
  return (
    <>
      <div className={owlClass}>
        <img src="https://stc-zoa-profile.zdn.vn/images/warning.svg" alt="" />
        <p className={`${owlClass}__text`}>{LangUtil.get('oa-disabled')}</p>
      </div>
    </>
  );
};

const ThreeDots = ({ isFollowed, onOpenPopupUnFollow, oaid, name, isShowCallBtn, msgCall }: any) => {
  const items = [
    {
      text: LangUtil.get('share-oa-to-friend'),
      onClick: () => NativeModel.openShareSheet(oaid),
    },
    {
      text: LangUtil.get('view-qr-code-oa'),
      onClick: () => NativeModel.openQR(oaid, name),
    },
  ];
  isShowCallBtn && items.splice(0, 0, {
    text: LangUtil.get('lb-call'),
    onClick: () => NativeModel.callToOA(oaid, msgCall),
  });
  return (
    <MuiThemeProvider>
      <BottomSheet
        items={
          isFollowed
            ? [
              ...items,
              {
                text: LangUtil.get('label-unfollow-oa'),
                onClick: () => onOpenPopupUnFollow(),
              },
              {
                text: LangUtil.get('label-report-oa'),
                color: "#FE5050",
                onClick: () => NativeModel.reportAbuse(oaid),
              },
            ]
            : [
              ...items,
              {
                text: LangUtil.get('report'),
                color: "#FE5050",
                onClick: () => NativeModel.reportAbuse(oaid),
              },
            ]
        }
        startHidden={true}
        element={
          <img src="https://stc-zoa-profile.zdn.vn/images/three-dots.svg" alt="" />
        }
      />
    </MuiThemeProvider>
  );
};

function App() {
  const [oa, setOA] = useState<OA>()
  const [isEmpty, setIsEmpty] = useState(true);
  const [isFollowed, setIsFollowed] = useState(false);
  const [isShowCertificationText, setIsShowCertificationText] = useState(false);
  const [isSeeMore, setIsSeeMore] = useState(true);
  const [isOpenPopupUnFollow, setIsOpenPopupUnFollow] = useState(false);
  const [isShowOutappPopup, setIsShowOutappPopup] = useState(false);
  const [errorMsg, setErrorMsg] = useState({ error: 0, msg: '' });
  const [isShowBtnLoadMoreDesc, setIsShowBtnLoadMoreDesc] = useState(false);
  const [isShowCallBtn, setIsShowCallBtn] = useState(false);


  const onOpenPopupUnFollow = () => setIsOpenPopupUnFollow(true);
  const onClosePopupUnFollow = () => setIsOpenPopupUnFollow(false);
  const onAgreeOpenZalo = (oaid: string) => {
    setIsShowOutappPopup(false);
    window.location.href = "https://zalo.me/" + oaid;
  }

  const fetchProfile = (oaid: string) => {
    OAModel.getProfile(oaid)
      .then(resp => {
        if (resp.error == 0) {
          let oa = resp.data;
          if (oa.is_disabled) {
            setIsEmpty(true)
          } else {
            setIsEmpty(false)
          }
          setOA(oa)
          setIsFollowed(oa.status == 0)
          isShouldShowCallBtn(oa)
        }
      })
  }

  const followOA = (oaid: string, continuteUrl: string) => {
    if (GlobalModel.isOpenOutApp()) {
      setIsShowOutappPopup(true)
      return;
    }

    setIsFollowed(true)
    OAModel.follow(oaid, continuteUrl)
      .then(resp => {
        if (resp.error >= 0) {
          if (resp.data && resp.data.is_can_redirect) {
            window.location.href = continuteUrl;
          } else {
            setIsFollowed(true)
            let appId = GlobalModel.getAppId();
            NativeModel.openChat(oaid, appId);
          }

        } else {
          if (resp.error == -20 || resp.error == -21 || resp.error == -22) {
            NativeModel.followOA(oaid, (e: any) => {
              try {
                if (e["error_code"] >= 0) {
                  setIsFollowed(true)
                  let appId = GlobalModel.getAppId();
                  NativeModel.openChat(oaid, appId);
                } else {
                  setIsFollowed(false)
                  var msg = LangUtil.toErrorMsg(-1);
                  setErrorMsg({ error: error_code, msg: msg })

                }
              } catch (e) {
                setIsFollowed(false)
                //Show error
              }
            })
          } else {
            setIsFollowed(false)
            var error_code = resp.error;
            var msg = LangUtil.toErrorMsg(error_code);
            setErrorMsg({ error: error_code, msg: msg })
          }
        }
      });
  }

  const unfollowOA = (oaid: string) => {
    if (GlobalModel.isOpenOutApp()) {
      setIsShowOutappPopup(true)
      return;
    }
    OAModel.unfollow(oaid)
      .then(resp => {
        if (resp.error >= 0) {
          setIsFollowed(false)
        } else {
          //show error
        }
      })
  }

  const sendMsg = (oaid: string) => {
    if (GlobalModel.isOpenOutApp()) {
      setIsShowOutappPopup(true)
      return;
    }
    TrackingModel.clickSmallMSGBtn(oaid)
    let appId = GlobalModel.getAppId();
    NativeModel.openChat(oaid, appId)
  }

  const click2Menu = (menu: Menu, oaid: string) => {
    switch (menu.type) {
      case 10: {
        if (menu.value.startsWith("https://zalo.me/app/link/zapps/") || menu.value.startsWith("http://zalo.me/app/link/zapps/")) {
          NativeModel.openMiniApp(menu.value);
        } else {
          window.location.href = menu.value
        }
        break;
      }
      case 53: {
        var json = JSON.parse(menu.value)
        window.open(`tel:${json.phoneCode}`)
        break;
      }
      case 21: {
        OAModel.call2Menu(menu.id, oaid)
          .then(resp => {
            if (resp.error == 0) {
              let appId = GlobalModel.getAppId();
              NativeModel.openChat(oaid, appId)
            } else {
              var msg = LangUtil.toErrorMsg(-100);
              setErrorMsg({ error: -100, msg: msg })
            }
          })
        break;
      }
    }
  }

  const isShowButtonLoadMore = () => {
    setTimeout(() => {
      var element = document.getElementById('desc-box');
      if (element) {
        if (element.offsetHeight < element.scrollHeight ||
          element.offsetWidth < element.scrollWidth) {
          setIsShowBtnLoadMoreDesc(true);
        } else {
          setIsShowBtnLoadMoreDesc(false)
        }
      }
    }, 500);
  }

  const isShouldShowCallBtn = (oa: OA) => {
    if (GlobalModel.isSupportCallOa() && oa.is_can_call && oa.call_msg) {
      setIsShowCallBtn(true)
    }
  }

  useEffect(() => {
    NativeModel.offMenu()
    NativeModel.disableWebviewCheckError()
    var oa = GlobalModel.getOA();
    if (oa) {
      setIsFollowed(oa.status == 0)
      setIsEmpty(false)
      setOA(oa)
      if (oa.is_disabled) {
        setIsEmpty(true)
      }
      fetchProfile(oa.oaid)
      isShowButtonLoadMore()
    }
  }, [])

  if (!oa) {
    return <div></div>
  }
  return (
    <div className={owlClass}>
      <img className={`${owlClass}__image`} src={oa.cover} alt="" />
      <div className={`${owlClass}__shop`}>
        <div className={`${owlClass}__shop__left`}>
          <div className={`${owlClass}__shop__left__avatar`}>
            <img src={oa.avatar} alt="" />
          </div>
          <div className={`${owlClass}__shop__left__info`}>
            <div className={`${owlClass}__shop__left__info__company`}>
              <h1 className={`${owlClass}__shop__left__info__company__name`}>
                {oa.name}
              </h1>
              {oa.is_verified &&
                <img
                  className={`${owlClass}__shop__left__info__company__verification`}
                  src="https://stc-zoa-profile.zdn.vn/images/verification.svg"
                  alt=""
                />
              }
            </div>
            {oa.cate &&
              <span className={`${owlClass}__shop__left__info__enterprise`}>
                {LangUtil.getCateName(oa.cate)}
              </span>
            }
          </div>
        </div>
        {!isEmpty &&
          <ThreeDots
            isFollowed={isFollowed}
            onOpenPopupUnFollow={onOpenPopupUnFollow}
            oaid={oa.oaid}
            name={oa.name}
            isShowCallBtn={isShowCallBtn}
            msgCall={oa.call_msg}
          />
        }
      </div>
      {errorMsg.error < 0 && <ErrorPopup open={errorMsg.error < 0} msg={errorMsg.msg} onCloseModal={() => { setErrorMsg({ error: 0, msg: '' }) }} />}
      {isShowOutappPopup && <OutAppPopup open={isShowOutappPopup} onCloseModal={() => { setIsShowOutappPopup(false) }} onAgree={() => { onAgreeOpenZalo(oa.oaid) }} />}
      {isEmpty && <Empty openProfileOA={() => setIsEmpty(false)} />}
      {!isEmpty && (
        <>
          <div className={`${owlClass}__group-btn`}>
            <div onClick={() => { sendMsg(oa.oaid) }} className={`${owlClass}__group-btn__item`}>{LangUtil.get('btn-label-send-msg')}</div>
            {isFollowed ? (
              <>
                <div
                  className={`${owlClass}__group-btn__item followed`}
                  onClick={onOpenPopupUnFollow}
                >
                  <img
                    style={{width: '50px', height: '40px'}}
                    src="https://stc-zoa-profile.zdn.vn/images/followed.svg"
                    alt=""
                  />
                </div>
                <Popup
                  oaName={oa.name}
                  open={isOpenPopupUnFollow}
                  onCloseModal={onClosePopupUnFollow}
                  onAgree={() => {
                    onClosePopupUnFollow();
                    unfollowOA(oa.oaid)
                  }}
                />
              </>
            ) : (
              <div
                className={`${owlClass}__group-btn__item follow`}
                onClick={() => followOA(oa.oaid, '')}
              >
                {LangUtil.get('label-follow-oa')}
              </div>
            )}
          </div>

          <div className={`${owlClass}__detail`}>
            {oa.is_verified &&
              <div className={`${owlClass}__detail__certification`}>
                <div className={`${owlClass}__detail__certification__oa`}>
                  <img
                    src="https://stc-zoa-profile.zdn.vn/images/verification.svg"
                    alt=""
                  />
                  <p className={`${owlClass}__detail__certification__oa__text`}>
                    {LangUtil.get('verification-desc')}
                  </p>
                </div>
                <img
                  className={`${owlClass}__detail__certification__chevron`}
                  src={`https://stc-zoa-profile.zdn.vn/images/chevron-${isShowCertificationText ? "up" : "down"
                    }.svg`}
                  alt=""
                  onClick={() =>
                    setIsShowCertificationText(!isShowCertificationText)
                  }
                />
              </div>
            }
            {isShowCertificationText && (
              <p className={`${owlClass}__detail__certification-text`}>
                {LangUtil.get('verification-desc-detail')}
              </p>
            )}
            <hr className={`${owlClass}__detail__divide`} />
            {oa.address &&

              <div className={`${owlClass}__detail__item`}>
                <img
                  src="https://stc-zoa-profile.zdn.vn/images/location.svg"
                  alt=""
                  onClick={() =>
                    setIsShowCertificationText(!isShowCertificationText)
                  }
                />
                <p className={`${owlClass}__detail__item__text`}>
                  {oa.address.name}
                </p>
              </div>
            }
            {oa.phone &&
              <div className={`${owlClass}__detail__item`}>
                <img
                  src="https://stc-zoa-profile.zdn.vn/images/phone.svg"
                  alt=""
                />
                <p onClick={() => window.open(`tel:${oa.phone}`)} className={`${owlClass}__detail__item__text primary`}>
                  {oa.phone}
                </p>
              </div>
            }
            {oa.store_status &&
              <StatusStore store_status={oa.store_status} />
            }
            {oa.website &&
              <div className={`${owlClass}__detail__item`}>
                <img src="https://stc-zoa-profile.zdn.vn/images/link.svg" alt="" />
                <a
                  className={`${owlClass}__detail__item__text primary`}
                  href={oa.website}
                >
                  {oa.website}
                </a>
              </div>
            }
            <p id="desc-box" className={`${owlClass}__detail__description ${isSeeMore ? "is-truncate" : ""}`} dangerouslySetInnerHTML={{ __html: oa.desc }}></p>
            {isShowBtnLoadMoreDesc &&
              <p className={`${owlClass}__detail__see-more`} onClick={() => setIsSeeMore(!isSeeMore)} >
                {isSeeMore ? LangUtil.get('see-more') : LangUtil.get('see-less')}
              </p>
            }
          </div>
          {oa.articles && <ListArticle oaid={oa.oaid} data={oa.articles} />}
        </>
      )}
    </div>
  );
}

export default App;
