import React from "react";
import Modal from "react-responsive-modal";
import "react-responsive-modal/styles.css";
import "../popup/styles.scss";


const owlClass = "popup";
const ERROR_LOCATION = -1;
const SUCCESS_LOCATION = 1;
const ERROR_REALTIME_PHOTO = -2;
const SUCCESS_REALTIME_PHOTO = 2;
export default class RealtimePhotoWidget extends React.PureComponent {

    constructor(props) {
        super(props)
        this.state = {
            error: 0,
            errorMsg: null,
            location: null,
        }
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(geoSuccessCallback, errorCallback);
        }
    }

    geoSuccessCallback = (position) => {
        this.setState({
            error: SUCCESS_LOCATION, location: {
                latitude: position.coords.latitude,
                longitude: position.coords.longitude
            }
        })

    }

    geoErrorCallback = (error) => {
        let errorMSg = null;
        if (error.code == 1) {
            errorMSg = "You've decided not to share your position, but it's OK. We won't ask you again.";
        } else if (error.code == 2) {
            errorMSg = "The network is down or the positioning service can't be reached.";
        } else if (error.code == 3) {
            errorMSg = "The attempt timed out before it could get the location data.";
        } else {
            errorMSg = "Geolocation failed due to unknown error.";
        }
        this.setState({ error: ERROR_LOCATION, errorMSg: errorMSg })
    }

    componentDidMount() {

    }

    render() {
        console.log(this.state.location)
        console.log(this.state.error)
        console.log(this.state.errorMsg)
        return (
            <Modal
                open={this.props.open}
                onClose={() => { }}
                center
                showCloseIcon={false}
                styles={{
                    modal: {
                        background: "rgba(242, 242, 242, 0.94)",
                        backdropFilter: "blur(54.3656px)",
                        borderRadius: "14px",
                        padding: "0",
                        maxWidth: "80%"
                    },
                }}
            >
                <div className={owlClass}>
                    <div className={`${owlClass}__wrapper`}>
                        <div className={`${owlClass}__wrapper__title`}>Realtime Photo</div>
                        <p className={`${owlClass}__wrapper__desc`}>
                            <span>1. Chấp nhận chia sẽ vị trí hiện tại </span>
                            <br />
                            <span>2. Chụp ảnh trực tiếp</span>
                        </p>
                    </div>
                    <div className={`${owlClass}__group-btn`}>
                        <div
                            className={`${owlClass}__group-btn__item right`}
                            onClick={() => { }}
                        >
                            Chụp ảnh
                        </div>
                    </div>
                </div>
            </Modal>
        )
    }
}
