import React from 'react'
import DateTimeUtil from '../utils/DateTimeUtil'
import LangUtil from '../utils/LangUtil'
import OAModel from '../model/OAModel'
import PropTypes from 'prop-types'
import TrackingModel from '../model/TrackingModel'

const owlClass = "App";

export default class ListArticle extends React.PureComponent {

    constructor(props) {
        super(props)
        this.state = {
            index: this.props.data.list.length,
            list: this.props.data.list
        }
        this.scroll = this.scroll.bind(this)
        this.getArticle = this.getArticle.bind(this)
        this.isLoading = false
    }

    componentWillMount() {
        window.addEventListener('scroll', this.scroll, false);
    }

    componentWillUnmount() {
        window.removeEventListener('scroll', this.scroll, false);
    }

    getArticle() {
        if (!this.isLoading) {
            this.isLoading = true;
            OAModel.getListArticle(this.state.index + 1, this.props.oaid)
                .then(resp => {
                    this.isLoading = false
                    if (resp.error == 0) {
                        let list = [...this.state.list]
                        list.push.apply(list, resp.data.list)
                        this.setState({ index: list.length, list: list })
                    }
                })
        }

    }

    scroll() {
        let innerHeight = window.innerHeight;
        let scrollHeight = document.scrollingElement.scrollHeight;
        let scrollTop = document.documentElement.scrollTop;
        if (scrollTop == 0) {
            scrollTop = document.getElementsByTagName("BODY")[0].scrollTop;
        }
        scrollTop = Math.abs(scrollTop)
        if (innerHeight + scrollTop + 500 >= scrollHeight) {
            this.getArticle()
        }
    }

    render() {
        return (
            <div className={`${owlClass}__blogs`}>
                <hr className={`${owlClass}__blogs__divide`} />
                <p className={`${owlClass}__blogs__title`}>{LangUtil.get('label-article-desc')}</p>
                {this.state.list.map((article) => (
                    <div onClick={() => { 
                        TrackingModel.viewArticle(this.props.oaid)
                        window.location.href = article.url 
                        }} key={article.id} className={`${owlClass}__blogs__article`}>
                        <Img src={article.thumb_url} alt="" />
                        <p className={`${owlClass}__blogs__article__date`}>
                            {DateTimeUtil.toString(article.created_time)}
                        </p>
                        <p className={`${owlClass}__blogs__article__title`}>
                            {article.title}
                        </p>
                    </div>
                ))}
            </div>
        )
    }
}

